module.exports = {"title":"Ruby MongoDB Driver API","date":"2010-03-07T00:00:00.000Z","tags":"documentation,mongodb,ruby","__content":"<h4 id=\"databases\">Databases</h4>\n<pre class=\"sh_ruby\">\ndb = Mongo::Connection.new(\"localhost\", 27017).db(\"mydb\")\n</pre>\n\n<h4 id=\"collections\">Collections</h4>\n<pre class=\"sh_ruby\">\ndb.collection_names.each { |name| puts name }\ncoll = db.collection(\"mycollection\")\n</pre>\n\n<h4 id=\"documents\">Documents</h4>\n<pre class=\"sh_ruby\">\nmydoc = coll.find_one()\nputs coll.count()\ncoll.find().each { |doc| puts doc.inspect }\ncoll.find({\"title\" => /^a/})\ncoll.find({},{:skip => 10, :limit => 20 })\n</pre>\n\n"}

